<?php
	$bathroom_keypad = array(array(1, 2, 3), array(4, 5, 6), array(7, 8, 9));
	
	$bathroom_keys = array("", 
	"",
	"", 
	"",
	"");

	function find_security_code_number($key, $keypad) {
		$key_length = strlen($key);
		$i = 0;
		$current_row_index = 1;
		$current_position_in_row = 1;

		while($i < $key_length) {
			
			switch($key[$i]) {
				case "U":
					if($current_row_index > 0) {
						$current_row_index--;
					}
					break;
				case "D":
					if($current_row_index < 2) {
						$current_row_index++;
					}
					break;
				case "R":
					if($current_position_in_row < 2) {
						$current_position_in_row++;
					}
					break;
				case "L":
					if($current_position_in_row > 0) {
						$current_position_in_row--;
					}
					break;
			}

			$i++;
		}

		return $keypad[$current_row_index][$current_position_in_row];
	}

	echo "The key code for the bathroom is: ";

	foreach($bathroom_keys as $key) {
		echo find_security_code_number($key, $bathroom_keypad);
	}

?>